import React from 'react'
import { StyleSheet, Text, View, Button } from 'react-native'
import { ListItem, Avatar } from "react-native-elements";
import { List } from 'react-native-paper';

const CustomListitem = ({ id, chatName, enterChat, navigation }) => {
    const [expanded, setExpanded] = React.useState(false);
    const [expandedReports, setExpandedReports] = React.useState(false);

    const handlePress = () => setExpanded(!expanded);
    const handlePressReports = () => setExpandedReports(!expandedReports);

    const signOutUser = () => {
        /*auth.signOut().then(() => {
            navigation.replace("Login");
        });*/
        navigation.replace("Login");
    };


    const content = (
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            height: '100%'
          }}
        >
          <Box
            sx={{
              alignItems: 'center',
              display: 'flex',
              flexDirection: 'column',
              p: 2
            }}
          >
            <Avatar
              component={RouterLink}
              src={user.avatar}
              sx={{
                cursor: 'pointer',
                width: 64,
                height: 64
              }}
              to="/app/account"
            />
            <Typography
              color="textPrimary"
              variant="h5"
            >
              {user.name}
            </Typography>
            <Typography
              color="textSecondary"
              variant="body2"
            >
              {user.jobTitle}
            </Typography>
          </Box>
          <Divider />
          <Box sx={{ p: 2 }}>
            <List>
              {items.map((item) => (
                <NavItem
                  href={item.href}
                  key={item.title}
                  title={item.title}
                  icon={item.icon}
                />
              ))}
            </List>
          </Box>
          <Box sx={{ flexGrow: 1 }} />
          <Box
            sx={{
              backgroundColor: 'background.default',
              m: 2,
              p: 2
            }}
          >
            <Typography
              align="center"
              gutterBottom
              variant="h4"
            >
              Need more?
            </Typography>
            <Typography
              align="center"
              variant="body2"
            >
              Upgrade to PRO version and access 20 more screens
            </Typography>
            <Box
              sx={{
                display: 'flex',
                justifyContent: 'center',
                pt: 2
              }}
            >
              <Button
                color="primary"
                component="a"
                href="https://react-material-kit.devias.io"
                variant="contained"
              >
                See PRO version
              </Button>
            </Box>
          </Box>
        </Box>
      );

      return (
        <>
          <Hidden lgDown>
            <Drawer
              anchor="left"
              open
              variant="persistent"
              PaperProps={{
                sx: {
                  width: 256,
                  top: 64,
                  height: 'calc(100% - 64px)'
                }
              }}
            >
              {content}
            </Drawer>
          </Hidden>
        </>
      );

    /*return (
        <ListItem>            
            <List.Section>           
                <List.Item
                title="Customers"
                description=""
                left={props => <List.Icon {...props} icon="account-multiple-outline" />}
             />
             <List.Accordion style={{ backgroundColor: "white" }}
                title="Cases"
                left={props => <List.Icon {...props} icon="cart" />}
                expanded={expanded}
                onPress={handlePress}>
                <List.Item title="First item" />
                <List.Item title="Second item" />
             </List.Accordion>
             <List.Item
                title="Saved cases"
                description=""
                left={props => <List.Icon {...props} icon="content-save-outline" />}
             />
            <List.Accordion style={{ backgroundColor: "white" }}
                title="Reports"
                left={props => <List.Icon {...props} icon="file-chart" />}
                expanded={expandedReports}
                onPress={handlePressReports}>
                <List.Item title="First item" />
                <List.Item title="Second item" />
             </List.Accordion>
             <List.Item
                title="Syncronize"
                description=""
                left={props => <List.Icon {...props} icon="sync" />}
             />

           </List.Section>

        </ListItem>
    )*/
}

export default CustomListitem

const styles = StyleSheet.create({})
