import React from 'react'
import { StyleSheet, Text, View } from 'react-native'
import { Button, Box, Avatar, Typography, Divider, ListItem, Drawer } from '@material-ui/core';
//import { List } from 'react-native-paper';

const items = [
    {
      href: '/app/dashboard',
      //icon: BarChartIcon,
      title: 'Dashboard'
    },
    {
      href: '/app/customers',
      //icon: UsersIcon,
      title: 'Customers'
    },
    {
      href: '/app/products',
      //icon: ShoppingBagIcon,
      title: 'Products'
    },
    {
      href: '/app/account',
      //icon: UserIcon,
      title: 'Account'
    },
    {
      href: '/app/settings',
      //icon: SettingsIcon,
      title: 'Settings'
    },
    {
      href: '/login',
      //icon: LockIcon,
      title: 'Login'
    },
    {
      href: '/register',
      //icon: UserPlusIcon,
      title: 'Register'
    },
    {
      href: '/404',
      //icon: AlertCircleIcon,
      title: 'Error'
    }
  ];


const CustomListitem = ({ id, chatName, enterChat, navigation }) => {
    const [expanded, setExpanded] = React.useState(false);
    const [expandedReports, setExpandedReports] = React.useState(false);

    const handlePress = () => setExpanded(!expanded);
    const handlePressReports = () => setExpandedReports(!expandedReports);

    const signOutUser = () => {
        /*auth.signOut().then(() => {
            navigation.replace("Login");
        });*/
        navigation.replace("Login");
    };


    const content = (
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column',
            height: '100%'
          }}
        >
          <Box
            sx={{
              alignItems: 'center',
              display: 'flex',
              flexDirection: 'column',
              p: 2
            }}
          >
            <Avatar
              src="https://cencup.com/wp-content/uploads/2019/07/avatar-placeholder.png"
              sx={{
                cursor: 'pointer',
                width: 64,
                height: 64
              }}
            />
            <Typography
              color="textPrimary"
              variant="h5"
            >
              Liz Gilbert
            </Typography>
            <Typography
              color="textSecondary"
              variant="body2"
            >
              Develop
            </Typography>
          </Box>
          <Divider />
          <Box sx={{ p: 2 }}>
            <ListItem
                disableGutters
                sx={{
                    display: 'flex',
                    py: 0
                }}
            >
            <Button
                sx={{
                    color: 'text.secondary',
                    fontWeight: 'medium',
                    justifyContent: 'flex-start',
                    letterSpacing: 0,
                    py: 1.25,
                    textTransform: 'none',
                    width: '100%',
                    '& svg': {
                        mr: 1
                    }
                }}
                to={href}
            >
                {Icon && (
                    <Icon size="20" />
                )}
            <span>
                {title}
            </span>
            </Button>
                </ListItem>
          </Box>
          <Box sx={{ flexGrow: 1 }} />
          <Box
            sx={{
              backgroundColor: 'background.default',
              m: 2,
              p: 2
            }}
          >
            <Typography
              align="center"
              gutterBottom
              variant="h4"
            >
              Need more?
            </Typography>
            <Typography
              align="center"
              variant="body2"
            >
              Upgrade to PRO version and access 20 more screens
            </Typography>
            <Box
              sx={{
                display: 'flex',
                justifyContent: 'center',
                pt: 2
              }}
            >
              <Button
                color="primary"
                component="a"
                href="https://react-material-kit.devias.io"
                variant="contained"
              >
                See PRO version
              </Button>
            </Box>
          </Box>
        </Box>
    );

      return (
        <>
            <Drawer
              anchor="left"
              open
              variant="persistent"
              PaperProps={{
                sx: {
                  width: 256,
                  top: 64,
                  height: 'calc(100% - 64px)'
                }
              }}
            >
              {content}
            </Drawer>
        </>
      );

    /*return (
        <ListItem>            
            <List.Section>           
                <List.Item
                title="Customers"
                description=""
                left={props => <List.Icon {...props} icon="account-multiple-outline" />}
             />
             <List.Accordion style={{ backgroundColor: "white" }}
                title="Cases"
                left={props => <List.Icon {...props} icon="cart" />}
                expanded={expanded}
                onPress={handlePress}>
                <List.Item title="First item" />
                <List.Item title="Second item" />
             </List.Accordion>
             <List.Item
                title="Saved cases"
                description=""
                left={props => <List.Icon {...props} icon="content-save-outline" />}
             />
            <List.Accordion style={{ backgroundColor: "white" }}
                title="Reports"
                left={props => <List.Icon {...props} icon="file-chart" />}
                expanded={expandedReports}
                onPress={handlePressReports}>
                <List.Item title="First item" />
                <List.Item title="Second item" />
             </List.Accordion>
             <List.Item
                title="Syncronize"
                description=""
                left={props => <List.Icon {...props} icon="sync" />}
             />

           </List.Section>

        </ListItem>
    )*/
}

export default CustomListitem

const styles = StyleSheet.create({})
