import React from 'react'
import { StyleSheet, Text, View, TextInput, SafeAreaView, FlatList, StatusBar } from 'react-native';
import { Icon } from 'react-native-elements';

const DATA = [
    {
      id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
      title: 'Kindred Hospital San Antonio',
      subTitle: 'jhon.peter@kindred.com'
    },
    {
      id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
      title: 'The children´s Hospital of San Antonio',
      subTitle: 'liam.arrieta@chsan.com'
    },
    {
      id: '58694a0f-3da1-471f-bd96-145571e29d72',
      title: 'Texas children´s Pavilion for Women',
      subTitle: 'violet.guzman@chpw.com'
    },
  ];
  
  const Item = ({ title, subTitle }) => (
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
      <Text style={styles.subTitle}>
          <Icon name='envelope' type='font-awesome' style={styles.icon}/>{subTitle}
      </Text>
    </View>
  );

const CustomersList = ({ navigation }) => {

    const renderItem = ({ item }) => (
        <Item title={item.title} subTitle={item.subTitle}/>
        
      );

    return (
        <View>
            <Text style={styles.label}>Customers</Text>
            <TextInput
                style={styles.input}
                placeholder="Search Customers by Name, Email and Address"
                keyboardType="text"
            />
            <FlatList
                data={DATA}
                renderItem={renderItem}
                keyExtractor={item => item.id}
            />
        </View>
    )
}

export default CustomersList

const styles = StyleSheet.create({
    container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    paddingLeft: 20,
    paddingRight: 20,
    paddingTop: 20,
    marginVertical: 4,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 22,
    color: '#5CACF2',
  },
  subTitle: {
      fontSize: 16,
      color: '#757575'
  },
  icon: {
    color: '#757575',
    marginRight: 15
  },
  input: {
    height: 40,
    margin: 12,
  }
})
