import React from 'react'
import { StyleSheet, Text, View, TextInput, TouchableOpacity, FlatList, StatusBar } from 'react-native';
import { Button, Grid, InputBase  } from '@material-ui/core';
import MailIcon from '@material-ui/icons/Mail';
import Modal from '@material-ui/core/Modal';

const DATA = [
    {
      id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
      title: 'Kindred Hospital San Antonio',
      subTitle: 'jhon.peter@kindred.com'
    },
    {
      id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
      title: 'The children´s Hospital of San Antonio',
      subTitle: 'liam.arrieta@chsan.com'
    },
    {
      id: '58694a0f-3da1-471f-bd96-145571e29d72',
      title: 'Texas children´s Pavilion for Women',
      subTitle: 'violet.guzman@chpw.com'
    },
  ];

const rand = () => {
  return Math.round(Math.random() * 20) - 10;
}

const getModalStyle = () => {
  const top = 50 + rand();
  const left = 50 + rand();

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`,
  };
}
  
const Item = ({ item, onPress }) => (
    <Grid container>
        <Grid item xs={8}>
            <TouchableOpacity style={styles.item} onPress={onPress}>
                <Text style={styles.title}>{item.title}</Text>
                <Text style={styles.subTitle}>
                    <MailIcon style={{ color: '#757575', marginRight: 8 }} fontSize="small"/>{item.subTitle}
                </Text> 
            </TouchableOpacity>
        </Grid>
        <Grid item xs={4} style={{ paddingTop: 20}}>
            <Button variant="outlined" style={{ color: '#5CACF2', borderColor: '#5CACF2', }}>
                Synchronize Products
            </Button>
            <Text>Last sysnc 05/20/2021</Text>
        </Grid>
    </Grid>
);

const CustomersList = ({ navigation }) => {

  //const classes = useStyles();
    // getModalStyle is not a pure function, we roll the style only on the first render
    const [modalStyle] = React.useState(getModalStyle);
    const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
    const renderItem = ({ item }) => {

      return (
        <Item
          item={item}
          onPress={handleOpen}
        />
      );
    };

    
  const body = (
    <div style={modalStyle} className={classes.paper}>
      <h2 id="simple-modal-title">Text in a modal</h2>
      <p id="simple-modal-description">
        Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
      </p>
  </div>
  );

    return (
        <View>
            <Text style={styles.label}>Customers</Text>
            <InputBase
              placeholder="Search…"
              inputProps={{ 'aria-label': 'search' }}
            />
            <FlatList
                data={DATA}
                renderItem={renderItem}
                keyExtractor={item => item.id}
            />
            <Modal
              open={open}
              onClose={handleClose}
              aria-labelledby="simple-modal-title"
              aria-describedby="simple-modal-description"
            >
              {body}
            </Modal>
        </View>
    )
}

export default CustomersList

const styles = StyleSheet.create({
    container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    paddingLeft: 20,
    paddingRight: 20,
    paddingTop: 20,
    marginVertical: 4,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 22,
    color: '#5CACF2',
  },
  subTitle: {
      fontSize: 16,
      color: '#757575'
  },
  icon: {
    color: '#757575',
    marginRight: 15
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
  },
})
